// 2002-tw-alliance

import styled from 'styled-components';
import images from '../../themes/images';

export const WrapTitle = styled.div`
  text-align: center;
  font-size: 20px;
  text-shadow: 0 0 13px #fbf6d5;
  font-weight: 600;
  letter-spacing: 2.75px;
  color: #fbf6d5;
  margin-top: 10.62vw;
  @media screen and (min-width: 800px) {
    margin-top: calc((800 / 828) * 88px);
  }
`;

export const WrapFilter = styled.div`
  margin: 0px auto;
  position: relative;
  background-color: rgba(9, 0, 59, 0.92);
  @media screen and (min-width: 800px) {
    padding: 30px 85px;
  }
  input {
    font-size: 1rem;
    width:100%;
    height:36px;
    display:flex;
    justify-content:center;
    line-height: 1;
    padding-left: 13px;
    padding-right: 60px;
    outline: none;
    color: #ffffff;
    margin: 0 auto 20px;
    border-radius: 50px;
    /* box-shadow: 0 0 10px 5px rgba(254, 65, 203, 0.5), inset 0 0 25px 0 #fe41cb; */
    border: none;
    position: relative;
    &::-webkit-input-placeholder {
      /* color: #f094ed; */
      line-height: 16px;
    }
    &:-moz-placeholder {
      /* color: #f094ed; */
      line-height: 16px;
    }
    &:-ms-input-placeholder {
      /* color: #f094ed; */
      line-height: 16px;
    }
  }
`;

export const NoData = styled.div`
  color: white;
  width: 100%;
  text-align: center;
  padding-bottom: 32px;
  font-weight: 600;
  line-height: 60px;
  padding-top: 15px;
`;

export const WrapLeaderboardItem = styled.div<{ withButton?: boolean }>`
  position: relative;
  height: 100%;
  overflow: hidden;
  .user-name {
    width: ${props => (props.withButton ? 'calc(100% - 85px)' : '100%')};
  }
`;

export const FrameBorder = styled.div`
  background: url(${images.rankBg}) center;
  background-size: 100% 100%;
  padding: 0 3px;
  position: relative;
  border-radius: 25px;
  border-bottom-left-radius: 40px;
  border-bottom-right-radius: 40px;
  &:before {
    content: '';
    position: absolute;
    top: -3px;
    left: 0;
    width: 100%;
    height: 25px;
    z-index: 1;
    background: url(${images.rankTopBg}) center no-repeat;
    background-size: 100%;
    background-position: top;
  }
  &:after {
    content: '';
    position: absolute;
    bottom: 7px;
    left: 0;
    width: 100%;
    height: 30px;
    z-index: 1;
    background: url(${images.rankBottomBg}) center no-repeat;
    background-size: 100%;
    background-position: bottom;
  }
`;

export const ToggleButton = styled.div<{ isOpen: boolean }>`
  width: 80px;
  height: 50px;
  position: absolute;
  top: -10px;
  left:45%;
  z-index: 11;
  background: url(${props => (props.isOpen ? images.missionTab : images.missionTab)})
    center no-repeat;
  background-size: 100%;
`;

export const WrapPanel = styled.div`
  background: url(${images.missionBG});
  background-size: cover;
  padding: 5px;
  height: 100%;
`;

export const WrapMission = styled.div<{ isChecked?: boolean }>`
  width: 100%;
  height: 80px;
  background-color:#090029;
  border: 1px solid #ce9914;
  border-radius: 15px;
  display: flex;
  flex-direction: column;
  margin-top: 5px;
  padding-left: 15px;
  position: relative;
  &:after {
    content: '';
    width: 45px;
    height: 49px;
    position: absolute;
    right: 15px;
    top: 15px;
    z-index: 1;
    background: url(${props =>
        props.isChecked ? images.checkIconAct : images.checkIcon})
      center no-repeat;
    background-size: cover;
  }
  .mission-title {
    width: 90px;
    height: 26px;
    border-radius: 15px;
    line-height: 26px;
    color: #ffffff;
    background:  url(${images.missionTitle});
    background-size: cover;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.5);
    text-align: center;
    margin: 10px 12px 0 0;
  }
  .mission-description {
    margin-top: 10px;
    line-height: 16px;
    font-size: 16px;
    text-align: left;
    margin-left: 10px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-content: center;
    margin-right: 70px;
  }
`;

export const WrapMetaScore = styled(WrapMission as any)`
  &:after {
    background: none;
  }
`;
