// 2008-tw-momo
import React, { useContext, useState, useCallback, useEffect } from 'react';

import {
  getDefaultDay,
  getCurrentTransLateLang,
  numberFormatWithDigits,
} from '@vmo/share/utils';
import { Tab40TimeState, Iframes } from '@vmo/web/constants/config';
import { Tab } from '@vmo/ui';
import { Tabs40Styled, CountDownStyled } from '@vmo/web/components/Tabs/styled';
import Tabs410 from '@vmo/web/components/Tabs/Tabs410';
import Tabs420 from '@vmo/web/components/Tabs/Tabs420';
import EventIntro from '@vmo/web/components/EventIntro';
import RankTitle from '@vmo/web/components/RankTitle';
import { api } from '@vmo/web/constants/api';
import { TranslationContext } from '@vmo/web/context/translationContext';
import LeaderboardContainer from '@vmo/web/components/LeaderboardContainer';
import { LeaderboardContainerWrapper } from '@vmo/web/components/LeaderboardContainer/LeaderboardContainer';
import * as Conditions from '@vmo/web/constants/conditions';
import Images from '@vmo/web/themes/images';
import { sortTab40 } from '@vmo/ui/hooks/api/sort';
import { createTabClickAction } from '17media-browser-spy';
import { trackingSource } from '@vmo/share/17appTrack';

const Tabs40 = () => {
  const translation = useContext(TranslationContext);
  useEffect(() => {
    trackingSource.track(
      createTabClickAction(
        '',
        `${translation.get('TAB_4')}/${translation.get('TAB_4_0')}`,
      ),
    );
  }, []);
  const [initialData, setInitialData] = useState({} || []);
  const tabs40Items = [
    {
      tabKey: Tab40TimeState[0].tabKey,
      title: null,
      tabText: `${translation.get('TAB_4')}/${translation.get('TAB_4_0')}`,
      tabs: null,
      countdown: {
        startDate: Tab40TimeState[0].startDate,
        endDate: Tab40TimeState[0].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t11,
      },
      rankTitle: translation.get('TAB_4_0'),
      leaderboard: {
        apis: [api.total.attend.before, api.total.bonus],
        sortFunc: sortTab40,
        condition: Conditions.rank511,
        iframe: null,
        realTime: 1000,
        style: {},
      },
    },
    {
      tabKey: Tab40TimeState[1].tabKey,
      title: null,
      tabText: `${translation.get('TAB_4')}/${translation.get('TAB_4_1')}`,
      tabs: () => <Tabs410 />,
    },
    {
      tabKey: Tab40TimeState[2].tabKey,
      title: null,
      tabText: `${translation.get('TAB_4')}/${translation.get('TAB_4_2')}`,
      tabs: () => <Tabs420 />,
    },
    {
      tabKey: Tab40TimeState[3].tabKey,
      title: null,
      tabText: `${translation.get('TAB_4')}/${translation.get('TAB_4_3')}`,
      tabs: null,
      rankTitle: translation.get('EVENT_INTRO_TITLE'),
      leaderboard: {
        iframe: Iframes.intro5,
        style: {},
        sortFunc: data => data,
      },
    },
  ];
  const defaultTabIndex = getDefaultDay(Tab40TimeState, 1);
  return (
    <Tabs40Styled active={Tab40TimeState[defaultTabIndex].tabKey}>
      {tabs40Items.map(item => (
        <Tab
          key={`tab00_${item.tabKey}`}
          tabKey={item.tabKey}
          title={item.title}
          tabText={item.tabText}
        >
          {item.tabs ? <item.tabs /> : null}
          {item.countdown &&
          item.countdown.startDate &&
          item.countdown.endDate ? (
            <CountDownStyled
              startDate={item.countdown.startDate}
              endDate={item.countdown.endDate}
              dateFormat={item.countdown.dateFormat}
              formatStr={item.countdown.formatStr}
            />
          ) : null}
          {item.eventIntro ? (
            <EventIntro
              title={item.eventIntro.title}
              iframe={item.eventIntro.iframe}
            />
          ) : null}
          {item.rankTitle ? <RankTitle title={item.rankTitle} /> : null}
          {item.leaderboard ? (
            <LeaderboardContainerWrapper styleInfo={item.leaderboard.style}>
              <LeaderboardContainer
                apis={item.leaderboard!.apis}
                initialData={initialData}
                sortFunc={(data: any) => {
                  const result = item.leaderboard!.sortFunc(data);
                  return result || [];
                }}
                condition={item.leaderboard!.condition}
                iframe={item.leaderboard.iframe}
                realTime={item.leaderboard!.realTime}
              />
            </LeaderboardContainerWrapper>
          ) : null}
        </Tab>
      ))}
    </Tabs40Styled>
  );
};

export default Tabs40;
