import React, { memo, useState, useEffect, useRef } from 'react';
import axios, { CancelTokenSource } from 'axios';
import { createPortal } from 'react-dom';

import { numberFormat, qs } from '@vmo/share/utils';
import {
  getEventoryDetails,
  getLeaderboardEventory,
} from '@vmo/share/services/leaderboardEventory.service';

import { THUMBNAIL_BASE_URL, DEFAULT_AVATAR } from '@vmo/share/CONST';
import { MissionPanelWrap, MissionItem, UserAvatar } from './style';

const MissionPanel = ({ condition }) => {
  const { missions, containerID } = condition;
  const queryString = qs<{
    userID: string;
  }>();
  const [isOpen, setIsOpen] = useState(false);
  const [missionScores, setMissionScores] = useState({});
  const [userDetail, setUserDetail] = useState(null);

  const source = useRef<CancelTokenSource>();
  const userInfoSource = useRef<CancelTokenSource>();

  useEffect(() => {
    if (queryString.userID) {
      localStorage.setItem('2010-xmas-userID', queryString.userID);
    }
    if (localStorage.getItem('2010-xmas-userID')) {
      const subKeys = localStorage.getItem('2010-xmas-userID');
      source.current = axios.CancelToken.source();
      userInfoSource.current = axios.CancelToken.source();
      const cancelToken = source.current.token;
      const userInfoCancelToken = userInfoSource.current.token;
      getLeaderboardEventory(
        containerID,
        userInfoCancelToken,
        1000,
        '',
        'GET',
      ).then(result => {
        if (result && result.length) {
          const user = result.find(item => item.userInfo.userID === subKeys);
          if (user) {
            setUserDetail(user);
          }
        }
      });
      getEventoryDetails(containerID, subKeys, cancelToken).then(result => {
        const detailList = {};
        if (result && result.data) {
          result.data.forEach(item => {
            if (item.meta && item.meta.eventoryKey) {
              detailList[item.meta.eventoryKey] = item.score;
            }
          });
        }
        setMissionScores(detailList);
      });
    }
    return () => {
      if (source.current) source.current.cancel();
      if (userInfoSource.current) userInfoSource.current.cancel();
    };
  }, [containerID, queryString.userID]);

  let avatar = '';
  if (userDetail && userDetail.userInfo) {
    avatar =
      userDetail.userInfo!.picture &&
      userDetail.userInfo!.picture!.startsWith('http')
        ? userDetail.userInfo!.picture
        : THUMBNAIL_BASE_URL + userDetail.userInfo!.picture;
  }

  if (!userDetail) {
    return null;
  }

  return (
    <MissionPanelWrap isOpen={isOpen} withUserInfo={!!userDetail}>
      <div>
        <div className="mission-button" onClick={() => setIsOpen(!isOpen)} />
        {userDetail && (
          <div className="mission-user">
            <UserAvatar
              style={{
                backgroundImage: `url(${avatar}), url(${DEFAULT_AVATAR})`,
                cursor: 'pointer',
              }}
            />

            <div className="mission-user_info">
              <div className="mission-user_info--user_name">
                {userDetail.userInfo.displayName || userDetail.userInfo.openID}
              </div>
              <div className="mission-user_info--user_score">
                {numberFormat(userDetail.score || 0)} 響叮噹
              </div>
            </div>
          </div>
        )}
        {missions.map(mission => {
          let isChecked = false;
          let score = 0;
          if (missionScores[mission.eventoryKey]) {
            isChecked = missionScores[mission.eventoryKey] > mission.threshold;
            score =
              missionScores[mission.eventoryKey] > mission.threshold
                ? mission.threshold
                : missionScores[mission.eventoryKey];
          }

          return (
            <MissionItem
              checked={isChecked}
              title={mission.title}
              key={mission.title}
            >
              {mission.description}
              <br />
              {mission.awardDescription}
              <div className="mission-score">
                目前累積數量 ( {score} / {mission.threshold} )
              </div>
            </MissionItem>
          );
        })}
      </div>
    </MissionPanelWrap>
  );
};

const PortalMissionPanel = props => {
  const el = document.createElement('div');
  useEffect(() => {
    const root = document.getElementById('__next');
    root.appendChild(el);
  }, [el]);

  return createPortal(<MissionPanel {...props} />, el);
};

export default memo(PortalMissionPanel);

