// 2006-tw-anniversary5
import React, { useMemo, useEffect, useContext } from 'react';

import { getDefaultDay, getCurrentTransLateLang } from '@vmo/share/utils';
import { Tab20TimeState, Iframes, TrackTab } from '@vmo/web/constants/config';
import { Tab } from '@vmo/ui';
import {
  Tabs20Styledxx,
  CountDownStyled,
  VoteButton,
  VoteContiner,
} from '@vmo/web/components/Tabs/styled';
import Tabs230 from '@vmo/web/components/Tabs/Tabs230';
import EventIntro from '@vmo/web/components/EventIntro';
import RankTitle from '@vmo/web/components/RankTitle';
import { api } from '@vmo/web/constants/api';
import { TranslationContext } from '@vmo/web/context/translationContext';
import LeaderboardContainer from '@vmo/web/components/LeaderboardContainer';
import { LeaderboardContainerWrapper } from '@vmo/web/components/LeaderboardContainer/LeaderboardContainer';
import * as Conditions from '@vmo/web/constants/conditions';
import { sortTab00, sortTab200, sortTab210 } from '@vmo/ui/hooks/api/sort';
import { createTabClickAction } from '17media-browser-spy';
import { trackingSource } from '@vmo/share/17appTrack';
import Tabs_intro1 from './Tabs_intro1';

const Tabs20 = () => {
  const translation = useContext(TranslationContext);
  useEffect(() => {
    trackingSource.track(
      createTabClickAction(
        '',
        `${translation.get('TAB_2')}/${translation.get('TAB_2_0')}`,
      ),
    );
  }, []);
  const tabs10Items = [
    {
      tabKey: Tab20TimeState[0].tabKey,
      title: 'Round1',
      tabText: `${translation.get('TAB_2')}/${translation.get('TAB_2_0')}`,
      tabs: null,
      countdown: {
        startDate: Tab20TimeState[0].startDate,
        endDate: Tab20TimeState[0].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t6,
      },
      rankTitle: translation.get('TAB_2_0'),
      vote: 'https://www.momoshop.com.tw/edm/cmmedm.jsp?npn=1vEHxiFEJkZl&n=1',
      leaderboard: {
        apis: [api.best.vote, api.best.bestAnchor],
        sortFunc: sortTab200,
        condition: Conditions.rank31,
        iframe: null,
        realTime: 0,
        style: {},
      },
    },
    {
      tabKey: Tab20TimeState[1].tabKey,
      title: 'Round2',
      tabText: `${translation.get('TAB_2')}/${translation.get('TAB_2_1')}`,
      tabs: null,
      countdown: {
        startDate: Tab20TimeState[1].startDate,
        endDate: Tab20TimeState[1].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t7,
      },
      rankTitle: translation.get('TAB_2_1'),
      leaderboard: {
        apis: [api.best.gift],
        sortFunc: sortTab210,
        condition: Conditions.rank32,
        iframe: null,
        realTime: 0,
        style: {},
      },
    },
    {
      tabKey: Tab20TimeState[2].tabKey,
      title: '線下總榜',
      tabText: `${translation.get('TAB_2')}/${translation.get('TAB_2_2')}`,
      tabs: null,
      countdown: {
        startDate: Tab20TimeState[2].startDate,
        endDate: Tab20TimeState[2].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t8,
      },
      rankTitle: translation.get('TAB_2_2'),
      leaderboard: {
        apis: [api.best.bestAnchor],
        sortFunc: sortTab00,
        condition: Conditions.rank33,
        iframe: null,
        realTime: 0,
        style: {},
      },
    },
    {
      tabKey: Tab20TimeState[3].tabKey,
      title: '活動說明',
      tabText: `${translation.get('TAB_2')}/${translation.get('TAB_2_3')}`,
      tabs: () => <Tabs_intro1 />,
    },
  ];
  const defaultTabIndex = getDefaultDay(Tab20TimeState, 2);
  return (
    <Tabs20Styledxx active={Tab20TimeState[defaultTabIndex].tabKey}>
      {tabs10Items.map(item => (
        <Tab
          key={`tab20_${item.tabKey}`}
          tabKey={item.tabKey}
          title={item.title}
          tabText={item.tabText}
        >
          {item.tabs ? <item.tabs /> : null}
          {item.countdown &&
          item.countdown.startDate &&
          item.countdown.endDate ? (
            <CountDownStyled
              startDate={item.countdown.startDate}
              endDate={item.countdown.endDate}
              dateFormat={item.countdown.dateFormat}
              formatStr={item.countdown.formatStr}
            />
          ) : null}
          {item.eventIntro ? (
            <EventIntro
              title={item.eventIntro.title}
              iframe={item.eventIntro.iframe}
            />
          ) : null}
          {item.vote ? (
            <VoteContiner>
              <VoteButton href={item.vote} />
            </VoteContiner>
          ) : null}
          {item.rankTitle ? <RankTitle title={item.rankTitle} /> : null}
          {item.leaderboard ? (
            <LeaderboardContainerWrapper styleInfo={item.leaderboard.style}>
              <LeaderboardContainer
                apis={item.leaderboard!.apis}
                sortFunc={(data: any) => {
                  const result = item.leaderboard!.sortFunc(data);
                  return result || [];
                }}
                condition={item.leaderboard!.condition}
                iframe={item.leaderboard.iframe}
                realTime={item.leaderboard!.realTime}
                hashCheck={item.leaderboard!.hashCheck}
              />
            </LeaderboardContainerWrapper>
          ) : null}
        </Tab>
      ))}
    </Tabs20Styledxx>
  );
};

export default Tabs20;
