// 2008-tw-momo
import React, { useMemo, useEffect, useContext } from 'react';

import { getDefaultDay, getCurrentTransLateLang } from '@vmo/share/utils';
import { Tab310TimeState, Iframes, TrackTab } from '@vmo/web/constants/config';
import { Tab } from '@vmo/ui';
import {
  Tabs310Styled,
  CountDownStyled,
} from '@vmo/web/components/Tabs/styled';
import EventIntro from '@vmo/web/components/EventIntro';
import RankTitle from '@vmo/web/components/RankTitle';
import { api } from '@vmo/web/constants/api';
import { TranslationContext } from '@vmo/web/context/translationContext';
import LeaderboardContainer from '@vmo/web/components/LeaderboardContainer';
import { LeaderboardContainerWrapper } from '@vmo/web/components/LeaderboardContainer/LeaderboardContainer';
import * as Conditions from '@vmo/web/constants/conditions';
import { sortTab00 } from '@vmo/ui/hooks/api/sort';
import { createTabClickAction } from '17media-browser-spy';
import { trackingSource } from '@vmo/share/17appTrack';
import { isNumericLiteral } from 'typescript';

const Tabs310 = () => {
  const translation = useContext(TranslationContext);
  useEffect(() => {
    trackingSource.track(
      createTabClickAction(
        '',
        `${translation.get('TAB_3')}/${translation.get(
          'TAB_3_1',
        )}/${translation.get('TAB_3_1_0')}`,
      ),
    );
  }, []);
  const tabs310Items = [
    {
      tabKey: Tab310TimeState[0].tabKey,
      title: null,
      tabText: `${translation.get('TAB_3')}/${translation.get(
        'TAB_3_1',
      )}/${translation.get('TAB_3_1_0')}`,
      tabs: null,
      countdown: {
        startDate: Tab310TimeState[0].startDate,
        endDate: Tab310TimeState[0].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t101,
      },
      rankTitle: translation.get('TAB_3_1_0'),
      leaderboard: {
        apis: [api.offline.r2.star],
        sortFunc: sortTab00,
        condition: Conditions.rank421,
        iframe: null,
        realTime: 1000,
        style: {},
      },
    },
    {
      tabKey: Tab310TimeState[1].tabKey,
      title: null,
      tabText: `${translation.get('TAB_3')}/${translation.get(
        'TAB_3_1',
      )}/${translation.get('TAB_3_1_1')}`,
      tabs: null,
      countdown: {
        startDate: Tab310TimeState[1].startDate,
        endDate: Tab310TimeState[1].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t102,
      },
      rankTitle: translation.get('TAB_3_1_1'),
      leaderboard: {
        apis: [api.offline.r2.daren],
        sortFunc: sortTab00,
        condition: Conditions.rank422,
        iframe: null,
        realTime: 1000,
        style: {},
      },
    },
    {
      tabKey: Tab310TimeState[2].tabKey,
      title: null,
      tabText: `${translation.get('TAB_3')}/${translation.get(
        'TAB_3_1',
      )}/${translation.get('TAB_3_1_2')}`,
      countdown: {
        startDate: Tab310TimeState[2].startDate,
        endDate: Tab310TimeState[2].endDate,
        dateFormat: 'MM/d (WN) hh:mm:ss',
        formatStr: translation.get('COUNTDOWN_COUNTING'),
      },
      eventIntro: {
        title: translation.get('EVENT_METHOD_TITLE'),
        iframe: Iframes.methods.t103,
      },
      rankTitle: translation.get('TAB_3_1_2'),
      leaderboard: {
        apis: [api.offline.r2.gaoshou],
        sortFunc: sortTab00,
        condition: Conditions.rank423,
        iframe: null,
        realTime: 1000,
        style: {},
      },
    },
  ];
  const defaultTabIndex = getDefaultDay(Tab310TimeState, 0);
  return (
    <Tabs310Styled active={Tab310TimeState[defaultTabIndex].tabKey}>
      {tabs310Items.map(item => (
        <Tab
          key={`tab10_${item.tabKey}`}
          tabKey={item.tabKey}
          title={item.title}
          tabText={item.tabText}
        >
          {item.tabs ? <item.tabs /> : null}
          {item.countdown &&
          item.countdown.startDate &&
          item.countdown.endDate ? (
            <CountDownStyled
              startDate={item.countdown.startDate}
              endDate={item.countdown.endDate}
              dateFormat={item.countdown.dateFormat}
              formatStr={item.countdown.formatStr}
            />
          ) : null}
          {item.eventIntro ? (
            <EventIntro
              title={item.eventIntro.title}
              iframe={item.eventIntro.iframe}
            />
          ) : null}
          {item.rankTitle ? <RankTitle title={item.rankTitle} /> : null}
          {item.leaderboard ? (
            <LeaderboardContainerWrapper styleInfo={item.leaderboard.style}>
              <LeaderboardContainer
                apis={item.leaderboard!.apis}
                sortFunc={(data: any) => {
                  const result = item.leaderboard!.sortFunc(data);
                  return result || [];
                }}
                condition={item.leaderboard!.condition}
                iframe={item.leaderboard.iframe}
                realTime={item.leaderboard!.realTime}
              />
            </LeaderboardContainerWrapper>
          ) : null}
        </Tab>
      ))}
    </Tabs310Styled>
  );
};

export default Tabs310;
